mapper.select:
select

from db_task
LEFT JOIN db_user a ON a.user_id=db_task.user_id


db_task.task_id = #{taskId,jdbcType=INTEGER}


AND db_task.user_id = #{userId,jdbcType=INTEGER}

select

from db_task
LEFT JOIN db_user a ON a.user_id=db_task.user_id


task_schedule = #{taskSchedule,jdbcType=INTEGER}


AND task_state = #{taskState,jdbcType=INTEGER}


AND task_type = #{taskType,jdbcType=INTEGER}


AND db_task.user_id = #{userId,jdbcType=VARCHAR}


order by task_createtime DESC


select

from db_task
LEFT JOIN db_user a ON a.user_id=db_task.user_id


task_schedule = #{Task.taskSchedule,jdbcType=INTEGER}


AND task_state = #{Task.taskState,jdbcType=INTEGER}


AND task_type = #{Task.taskType,jdbcType=INTEGER}


AND db_task.user_id = #{userId,jdbcType=VARCHAR}



AND task_createtime >= #{startTime}




AND task_createtime <= #{endTime}



order by task_createtime DESC
